import React, { ChangeEvent, useEffect, useState } from "react";
import "./App.scss";
import { Card } from "./components/card/card.component";
import { FetchApi } from "./utility/api.utility";

export type Product = {
    id: number;
    name: string;
    color: string;
    year: number;
    pantone_value: string;
};

const App = () => {
    const [product, setProduct] = useState<Product[]>([]);
    const [search, setSearch] = useState<string>("");

    useEffect(() => {
        const fetchData = async () => {
            const data = await FetchApi();
            setProduct(data.data);
        };
        fetchData();
    }, []);

    const handleChange = (object: ChangeEvent<HTMLInputElement>) => {
        const target: HTMLInputElement = object.target;

        console.log(target.value);
    };

    return (
        <div className="App">
            <div className="header-container">
                <h1>Api search</h1>
            </div>
            <div className="input-container">
                <input
                    type="number"
                    name="number"
                    placeholder="Search by Id"
                    onChange={(x) => handleChange(x)}
                />
            </div>

            <button onClick={() => console.log(product)}>Check products</button>

            <div className="card-container">
                {product.map((x) => (
                    <Card key={x.id} product={x} />
                ))}
            </div>
        </div>
    );
};

export default App;
