import React, { useEffect, useState } from "react";
import "./App.scss";
import Search from "./components/search/search";
import ProductCard from "./components/productCard/productCard";
import axios from "axios";

type productData = {
    data: Product[];
};

export type Product = {
    id: string;
    name: string;
    year: number;
    color: string;
    pantone_value: string;
};

const App = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [error, setError] = useState("");
    const [filterId, setFilterId] = useState("");
    const [search, setSearch] = useState("");

    const searchSerializer = (target: HTMLInputElement) => {
        const targetValue: string = target.value;
        setSearch(targetValue);
        console.log(targetValue);
    };

    const fetchData = async () => {
        const response = axios
            .get("https://reqres.in/api/products?id=")
            .then((res) => {
                const newResponse: Product[] = res.data.data;
                console.log("Response: ", newResponse); // returns list of 6 elements
                setProducts(newResponse);
            })
            .catch((err) => {
                console.log("Error!! ", err);
            });

        return response;
    };

    useEffect(() => {
        fetchData();
    }, []);

    return (
        <div className="MainDiv">
            <Search
                inputClass="search-box"
                placeholder="Search by id"
                onChangeHandler={(x) => {
                    searchSerializer(x.target);
                }}
            />

            <ul>
                {products
                    .filter((product) => {
                        if (search === "") {
                            return product;
                        } else if (
                            product.id
                                .toLowerCase()
                                .includes(search.toLowerCase())
                        ) {
                            return product;
                        }
                    })
                    .map((product) => (
                        <ProductCard card={product} />
                    ))}
            </ul>
        </div>
    );
};

export default App;
