import { Table } from "../table/table.component";
import { useAppSelector, useAppDispatch } from "../../store/hooks";
import "./pagedtable.scss";
import { useEffect } from "react";
import { Product } from "../../App";
import { FetchApi } from "../../utility/api.utility";
import { setProducts } from "../../store/reducers/products.reducer";
import { setPageAmout } from "../../store/reducers/tablePages.reducer";
export const PagedTable = () => {
    const useSelector = useAppSelector;
    const dispatch = useAppDispatch();
    const products = useSelector((state) => state.products).products;
    const { current_page } = useSelector((state) => state.tableInfo);

    useEffect(() => {
        FetchApi("?page=" + current_page + "&per_page=5").then((data) => {
            dispatch(setProducts(data.data));
        });
    }, [current_page]);

    const TablePage = () => {
        return (
            <>
                {products.map((x) => (
                    <Table key={x.id} product={x} />
                ))}
            </>
        );
    };

    return (
        <>
            <tbody>
                <TablePage />
            </tbody>
        </>
    );
};
