import React, { MouseEvent, useEffect, useState } from "react";
import "./App.css";
import Search from "./components/search/search";
import { getData } from "./utility/data.utilits";
import ProductCard from "./components/productCard/productCard";
import { apiUrl } from "./utility/data.utilits";

type productData = {
    data: Product[];
};

export type Product = {
    id: string;
    name: string;
    year: number;
    color: string;
    pantone_value: string;
};

const App = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [filterId, setFilterId] = useState("");
    const [searchResult, setSearchResult] = useState("");

    const fetch = async (fetchId: string = "") => {
        const data = await getData<productData>(`${apiUrl}?id=${fetchId}`);
        console.log(data.data);
        return setProducts(data.data);
    };

    useEffect(() => {
        fetch();
    }, []);

    useEffect(() => {
        fetch(searchResult);
    }, [searchResult]);

    return (
        <>
            {/* {products.forEach((x) => (
                <p>{x.name}</p>
            ))} */}
            <Search
                onChangeHandler={(x) => {
                    const value: number = x.target.value;
                }}
            />
            <button> Search </button>
        </>
    );
};

export default App;
