import { useState } from "react";
import { Product } from "../../App";
import "./table.scss";

import { useAppSelector, useAppDispatch } from "../../store/hooks";

export const Table = (product: any) => {
    const dispatch = useAppDispatch();

    const { id, name, color, year, pantone_value } = product.product as Product;

    const [aboutProduct, setAboutProduct] = useState<Product>(
        product.product as Product
    );

    const toggleAbout = (): void => {
        setAboutProduct(product.product as Product);
    };

    const toggleView = (): void => {
        dispatch({ type: "TOGGLE_VIEW" });
        console.log("TOGGLE_VIEW");
    };

    const style = {
        backgroundColor: color,
    };
    return (
        <tr style={style} onClick={() => toggleView()}>
            <td>{id}</td>
            <td>{name}</td>
            <td>{color}</td>
            <td>{year}</td>
            <td>{pantone_value}</td>
        </tr>
    );
};

