import "./about.scss";
import { Product } from "../../../App";
import { useAppSelector } from "../../../store/hooks";
import { useEffect } from "react";

export const AboutView = () => {
    const useSelector = useAppSelector;
    const product = useSelector(
        (state) => state.aboutItem.selectedProduct
    ) as Product;

    const { id, name, color, year, pantone_value } = product;

    const style = {
        color: color,
    };

    return (
        <>
            <div className="about-view">
                <div>
                    <button className="close-btn">X</button>
                </div>
                <p>ID: {id}</p>
                <p>NAME: {name}</p>
                <p>
                    COLOR: <span style={style}>{color}</span>
                </p>
                <p>YEAR: {year}</p>
                <p>PANTONE VALUE: {pantone_value}</p>
            </div>
            <button onClick={() => console.log(product)}>Show Product</button>
        </>
    );
};
