import { Table } from "../table/table.component";
import { useAppSelector } from "../../store/hooks";
import "./pagedtable.scss";
import { useEffect } from "react";
import { Product } from "../../App";
import { FetchApi } from "../../utility/api.utility";

export const PagedTable = () => {
    const useSelector = useAppSelector;
    // const products = useSelector((state) => state.products).products;
    const { current_page, items_per_page } = useSelector(
        (state) => state.tableInfo
    );
    let products: Product[];

    useEffect(() => {
        FetchApi("?page=" + current_page + "&per_page=" + items_per_page).then(
            (data) => {
                products = data.data;
            }
        );
    }, []);

    const TablePage = () => {
        const min_id_allowed = (current_page - 1) * 5 + 1;
        const max_id_allowed = current_page * 5;

        return (
            <>
                {/* {products.map((x) => {
                    if (x.id <= max_id_allowed && x.id >= min_id_allowed)
                        return <Table key={x.id} product={x} />;
                })} */}
            </>
        );
    };

    return (
        <>
            <tbody>
                <TablePage />
            </tbody>
        </>
    );
};
