import React, { useEffect, useState } from "react";
import "./App.scss";
import Search from "./components/search/search";
import ProductCard from "./components/productCard/productCard";
import axios from "axios";
type productData = {
    data: Product[];
};

export type Product = {
    id: string;
    name: string;
    year: number;
    color: string;
    pantone_value: string;
};

const App = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [error, setError] = useState("");
    const [filterId, setFilterId] = useState("");
    const [search, setSearch] = useState("");

    useEffect(() => {
        fetchData();
    }, []);

    const searchSerializer = (target: HTMLInputElement) => {
        const targetValue: string = target.value;
        setSearch(targetValue);
        console.log(targetValue);
    };

    const fetchData = async (): Promise<void> => {
        const response: Promise<void> = axios
            .get("https://reqres.in/api/products?id=")
            .then((res) => {
                if (res.data.data == []) fetchData();
                setProducts(res.data.data);
                console.log("Products has been updated! ", products);
            })
            .catch((err) => {
                console.log("Error!! ", err);
            });

        return response;
    };

    return (
        <div className="MainDiv">
            <Search
                inputClass="search-box"
                placeholder="Search by id"
                onChangeHandler={(x) => {
                    searchSerializer(x.target);
                }}
            />
        </div>
    );
};

export default App;
