import React, { useEffect, useState } from "react";
import "./App.scss";
import Search from "./components/search/search";
import { getData } from "./utility/data.utilits";
import ProductCard from "./components/productCard/productCard";
import { apiUrl } from "./utility/data.utilits";

type productData = {
    data: Product[];
};

export type Product = {
    id: string;
    name: string;
    year: number;
    color: string;
    pantone_value: string;
};

const App = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [filterId, setFilterId] = useState("");
    const [searchResult, setSearchResult] = useState("");

    const fetch = async (fetchId: string = "") => {
        const data = await getData<productData>(`${apiUrl}?id=${fetchId}`);
        console.log(data.data);
        return setProducts(data.data);
    };

    useEffect(() => {
        fetch();
    }, []);

    useEffect(() => {
        fetch(searchResult);
    }, [searchResult]);

    return (
        <div className="MainDiv">
            <Search
                inputClass="search-box"
                placeholder="Search by id"
                onChangeHandler={(x) => {
                    const target: HTMLInputElement = x.target;

                    target.value =
                        target.value.length > 2
                            ? target.value.slice(0, 2)
                            : target.value;
                    target.value = +target.value > 12 ? "12" : target.value;

                    setSearchResult(target.value);
                }}
            />
            <>{products}</>
        </div>
    );
};

export default App;
