import { Table } from "../table/table.component";
import { useAppSelector } from "../../store/hooks";
import { useEffect, useState } from "react";
import { ceil } from "mathjs";
import "./pagedtable.scss";

export const PagedTable = () => {
    const [tablePageSize, setTablePageSize] = useState<number>(2);
    const [tablePage, setTablePage] = useState<number>(1);
    const [tableSize, setTableSize] = useState<number>(5);
    const useSelector = useAppSelector;
    const products = useSelector((state) => state.products).products;
    //
    //
    useEffect(() => {
        setTableSize(products.length);
    }, []);

    const Pages = (amount: number): number[] => {
        let pages: number[] = [];
        for (let i = 1; i < tablePages + 1; i++) {
            pages.push(i);
        }
        return pages;
    };

    const tablePages = ceil(tableSize / tablePageSize); // divide by items per page ( 5 )

    const TablePage = (page_number: any) => {
        return (
            <>
                {products.map((x) => (
                    <Table key={x.id} product={x} />
                ))}
            </>
        );
    };

    return (
        <>
            <tbody>
                <TablePage />

                {/* <tr className="pagination">
                    <td colSpan={5}>
                        {Pages(tablePageSize).map((x) => (
                            <p key={x}>{x}</p>
                        ))}
                    </td>
                </tr> */}
            </tbody>
        </>
    );
};
