import { createSlice } from "@reduxjs/toolkit";

interface AboutViewState {
    isVisible: boolean;
}

const initialState: AboutViewState = {
    isVisible: false,
};

export const aboutViewSlice = createSlice({
    name: "aboutView",
    initialState,
    reducers: {
        toggleAboutView: (state) => {
            state.isVisible = !state.isVisible;
        },
    },
});

export const { toggleAboutView } = aboutViewSlice.actions;

export default aboutViewSlice.reducer;
