import { useState } from "react";
import { Product } from "../../App";
import "./table.scss";

export const Table = (product: any) => {
    const { id, name, color, year, pantone_value } = product.product as Product;

    const [showAbout, setShowAbout] = useState<boolean>(false);
    const [aboutProduct, setAboutProduct] = useState<Product>(
        product.product as Product
    );

    const toggleAbout = (): void => {
        setShowAbout(!showAbout);
        setAboutProduct(product.product as Product);
    };

    const style = {
        backgroundColor: color,
    };
    return (
        <tr style={style} onClick={() => toggleAbout}>
            <td>{id}</td>
            <td>{name}</td>
            <td>{color}</td>
            <td>{year}</td>
            <td>{pantone_value}</td>
        </tr>
    );
};

