import "./paginator.scss";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { ceil } from "mathjs";
import {
    setCurrentPage,
    setItemsPerPage,
} from "../../store/reducers/tablePages.reducer";
import { useEffect } from "react";
export const Paginator = () => {
    const dispatch = useAppDispatch();
    const useSelector = useAppSelector;

    const { products } = useSelector((state) => state.products);
    const { current_page, items_per_page } = useSelector(
        (state) => state.tableInfo
    );

    const Pages = (amount: number): number[] => {
        let pages: number[] = [];
        for (let i = 1; i < amount + 1; i++) {
            pages.push(i);
        }
        return pages;
    };

    const pageChangeHandler = (x: number) => {
        dispatch(setCurrentPage(x));
    };
    const incrementPage = () => {
        if (current_page + 1 < ceil(products.length / items_per_page)) {
            dispatch(setCurrentPage(current_page + 1));
        }
    };
    const decrementPage = () => {
        if (current_page - 1 > 0) {
            dispatch(setCurrentPage(current_page - 1));
        }
    };

    return (
        <div className="paginator-container">
            <ul>
                {Pages(ceil(products.length / items_per_page)).map((x) => (
                    <li key={x} onClick={() => pageChangeHandler(x)}>
                        {x == current_page ? (
                            <p className="active">{x}</p>
                        ) : (
                            <p>{x}</p>
                        )}
                    </li>
                ))}
            </ul>
            <div className="pages-arrow">
                <p onClick={() => incrementPage()}> {"< < previous"} </p>
                <p onClick={() => decrementPage()}> {"next > >"} </p>
            </div>
            <div className="page-info">
                <p>Page {current_page}</p>
                <p>
                    Items per page &nbsp;
                    <select
                        name="perpage"
                        id="items-per-page"
                        onChange={(x) => {
                            dispatch(setItemsPerPage(+x.target.value));
                            dispatch(setCurrentPage(1));
                        }}
                        defaultValue={items_per_page}
                    >
                        <option value="1">1</option>
                        <option value="2">2</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                        <option value="5">5</option>
                        <option value="6">6</option>
                        <option value="7">7</option>
                        <option value="8">8</option>
                        <option value="9">9</option>
                        <option value="10">10</option>
                        <option value="11">11</option>
                        <option value="12">12</option>
                        <option value="13">13</option>
                        <option value="14">14</option>
                        <option value="15">15</option>
                    </select>
                </p>
            </div>
        </div>
    );
};
