import { Table } from "../table/table.component";
import { useAppSelector } from "../../store/hooks";
import { useEffect, useState } from "react";
import { ceil } from "mathjs";
import "./pagedtable.scss";

export const PagedTable = () => {
    const useSelector = useAppSelector;
    const products = useSelector((state) => state.products).products;
    const { current_page, items_per_page } = useSelector(
        (state) => state.tableInfo
    );

    const TablePage = () => {
        const max_id_allowed = current_page * items_per_page;

        return (
            <>
                {products.map((x) => {
                    if (x.id <= max_id_allowed)
                        return <Table key={x.id} product={x} />;
                })}
            </>
        );
    };

    return (
        <>
            <tbody>
                <TablePage />
            </tbody>
        </>
    );
};
