import "./paginator.scss";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { ceil } from "mathjs";
import { setCurrentPage } from "../../store/reducers/tablePages.reducer";
export const Paginator = () => {
    const dispatch = useAppDispatch();
    const useSelector = useAppSelector;

    const { products } = useSelector((state) => state.products);
    const { current_page, items_per_page } = useSelector(
        (state) => state.tableInfo
    );

    const Pages = (amount: number): number[] => {
        let pages: number[] = [];
        for (let i = 1; i < amount + 1; i++) {
            pages.push(i);
        }
        return pages;
    };

    const pageChangeHandler = (x: number) => {
        console.log("Changing page to " + x);
        dispatch(setCurrentPage(x));
    };

    return (
        <div className="paginator-container">
            <ul>
                {Pages(ceil(products.length / items_per_page)).map((x) => (
                    <li key={x} onClick={() => pageChangeHandler(x)}>
                        <p>{x}</p>
                    </li>
                ))}
            </ul>
            <div className="page-info">
                <p>Page {current_page}</p>
                <p>
                    Items per page{" "}
                    <select name="cars" id="cars">
                        <option value="volvo">Volvo</option>
                        <option value="saab">Saab</option>
                        <option value="opel">Opel</option>
                        <option value="audi">Audi</option>
                    </select>{" "}
                </p>
            </div>
        </div>
    );
};
