import "./about.scss";
import { Product } from "../../../App";
import { useAppSelector } from "../../../store/hooks";

export const AboutView = () => {
    const useSelector = useAppSelector;
    const product = useSelector((state) => state.aboutItem.selectedProduct);
    const { id, name, color, year, pantone_value } = product as Product;

    return (
        <>
            <div className="about-view">
                <p>id : {id}</p>
                <p>{name}</p>
                <p>{color}</p>
                <p>{year}</p>
                <p>{pantone_value}</p>
            </div>
            <button>Show Product</button>
        </>
    );
};
