import React, { ChangeEvent, useEffect, useState } from "react";
import "./App.scss";
import { Table } from "./components/table/table.component";
import { FetchApi } from "./utility/api.utility";
import { AboutView } from "./components/table/about/about.component";
import { useAppSelector } from "./store/hooks";

export type Product = {
    id: number;
    name: string;
    color: string;
    year: number;
    pantone_value: string;
};

const App = () => {
    const [product, setProduct] = useState<Product[]>([]);
    const [error, setError] = useState<string>("");
    const [responseStatus, setResponseStatus] = useState<string>("");
    const useSelector = useAppSelector;
    const aboutView = useSelector((state) => state.aboutView.isVisible);

    const fetchData = async (params?: string) => {
        const data = await FetchApi(params ? params : "");

        if (data.errorMessage) {
            // console.log(data.error);
            setResponseStatus("" + data.errorMessage);
            return setProduct([]);
        }
        setResponseStatus("");
        if (data.data.length === undefined) {
            setProduct([data.data]);
            return;
        }

        return setProduct(data.data);
    };

    useEffect(() => {
        fetchData();
    }, []);

    // export const useForm = <T,>(callback: () => Promise<any>, initalState: T)

    return (
        <div className="App">
            {aboutView && <AboutView />}
            {/* <AboutView /> */}

            <div className="header-container">
                <h1>Api search</h1>
            </div>

            <form
                action=""
                onSubmit={async (e: any) => {
                    e.preventDefault();
                    const numField: HTMLInputElement = e.target.number;

                    if (isNaN(+numField.value)) {
                        setError("Enter a valid number");
                    } else if (+numField.value < 0) {
                        setError("Only positive numbers are allowed");
                    } else if (
                        +numField.value.length > 1 ||
                        +numField.value.length < 0
                    ) {
                        setError("Only 1 and 2 digit numbers are allowed");
                    } else if (+numField.value.length < 0) {
                        setError("Only 2 digit numbers are allowed");
                    } else {
                        await fetchData(`?id=${numField.value}`);
                        setError("");

                        numField.value = "";
                        return;
                    }
                    return setError("Unknown error");
                }}
            >
                <input type="number" name="number" placeholder="Search by Id" />
                <button className="search-btn" type="submit">
                    âž”
                </button>
            </form>

            {responseStatus ? (
                <h3 className="response-error">{responseStatus}</h3>
            ) : (
                <div className="table-container">
                    <table>
                        <thead>
                            <tr>
                                <th>Id</th>
                                <th>Name</th>
                                <th>Color</th>
                                <th>Year</th>
                                <th>Pantone value</th>
                            </tr>
                        </thead>
                        <tbody>
                            {product.map((x) => (
                                <Table key={x.id} product={x} />
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default App;
