import React, { useEffect, useState } from "react";
import "./App.scss";
import Search from "./components/search/search";
import { getData } from "./utility/data.utilits";
import ProductCard from "./components/productCard/productCard";
import { apiUrl } from "./utility/data.utilits";

type productData = {
    data: Product[];
};

export type Product = {
    id: string;
    name: string;
    year: number;
    color: string;
    pantone_value: string;
};

const App = () => {
    const [products, setProducts] = useState<Product[]>([]);
    const [filterId, setFilterId] = useState("");

    const [search, setSearch] = useState("");

    const fetch = async (fetchId: string = "") => {
        console.log(`Fetching with ${fetchId}`);
        const data = await getData<productData>(`${apiUrl}?id=${fetchId}`);
        // console.log(data.data);
        return setProducts(data.data);
    };

    const searchSerializer = (target: HTMLInputElement) => {
        const targetValue: string = target.value;
        setSearch(targetValue);
        console.log(targetValue);
    };

    useEffect(() => {
        fetch(search);
    }, [search]);

    return (
        <div className="MainDiv">
            <Search
                inputClass="search-box"
                placeholder="Search by id"
                onChangeHandler={(x) => {
                    searchSerializer(x.target);
                }}
            />
            <>{/* <p>{products[0].name}</p> */}</>
        </div>
    );
};

export default App;
