import React, { ChangeEvent, useEffect, useState } from "react";
import "./App.scss";
import { Table } from "./components/table/table.component";
import { FetchApi } from "./utility/api.utility";
import { AboutView } from "./components/table/about/about.component";
import { useAppSelector } from "./store/hooks";
export type Product = {
    id: number;
    name: string;
    color: string;
    year: number;
    pantone_value: string;
};

const App = () => {
    const [product, setProduct] = useState<Product[]>([]);

    useEffect(() => {
        const fetchData = async () => {
            const data = await FetchApi();
            setProduct(data.data);
        };
        fetchData();
    }, []);

    const handleChange = (object: ChangeEvent<HTMLInputElement>) => {
        const target: HTMLInputElement = object.target;

        console.log(target.value);
    };

    return (
        <div className="App">
            <AboutView />

            <div className="header-container">
                <h1>Api search</h1>
            </div>
            <div className="input-container">
                <input
                    type="number"
                    name="number"
                    placeholder="Search by Id"
                    onChange={(x) => handleChange(x)}
                />
            </div>

            <button onClick={() => console.log(product)}>Check products</button>

            <div className="table-container">
                <table>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Name</th>
                            <th>Color</th>
                            <th>Year</th>
                            <th>Pantone value</th>
                        </tr>
                    </thead>
                    <tbody>
                        {product.map((x) => (
                            <Table key={x.id} product={x} />
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default App;
